INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('de67c702-4e01-4f9c-bb5c-6077ebfb9e41', '6ae18596-2b31-4f20-8e4f-7a9541ef7dcb', 12, 'The Makefile defines a separate `venv` target that creates a virtual environment, but the `setup` target only runs `pip install -e .` without ensuring the venv is activated. If a developer runs `make setup` without first running `make venv`, the packages are installed globally, which can lead to permission errors or conflicts with system packages. This is a nonobvious dependency that can trip up newcomers.', 'gotcha', 'Makefile', 'auto-extracted,importance:high,session:exploration', 8, NULL, '2026-01-24T00:23:43.125176', 1769232223, 1, '4ea738d8c81563d3', 'morningstar_chris');
INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('bf618576-a4a7-4556-a4e7-f11e9191a3d2', '6ae18596-2b31-4f20-8e4f-7a9541ef7dcb', 12, 'There is a `tool-reinstall` target that removes all installed tools (via `pip uninstall -y -r requirements.txt`) and then reinstalls them. This target is useful for resetting the development environment, but it also deletes any locally built wheels or editable installs, so it should be used with caution.', 'discovery', 'Makefile', 'auto-extracted,importance:medium,session:exploration', 5, NULL, '2026-01-24T00:23:43.197897', 1769232223, 1, 'db7882a81635d7b8', 'morningstar_chris');
