INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('4d7ba07f-9bdf-44ab-af98-b11fcf052723', '23a3b5d1-4021-425d-9b63-812aefc12ccd', 42, 'Hook actions that reference environment variables (e.g., `{{PORT}}`) are only substituted when the daemon starts. If the port changes at runtime (e.g., due to dynamic allocation), the hooks will still point to the old port, causing 404s for injected commands.', 'gotcha', '/Users/chris/Repos/open-agent-kit/src/open_agent_kit/features/codebase_intelligence/hooks/gemini/hooks.json', 'auto-extracted,importance:medium,session:exploration', 5, NULL, '2026-01-25T16:43:13.445075', 1769377393, 1, '1d64075df425ebd9', 'morningstar_chris');
