INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('81205c8f-c81c-405f-af3a-991d0bec1645', '23a3b5d1-4021-425d-9b63-812aefc12ccd', 42, 'The injection mechanism relies on a global `MemoryStore` that is shared across all agents. If multiple agents run concurrently, they may overwrite each other''s context entries because the store uses simple string keys without namespacing.', 'gotcha', '/Users/chris/Repos/open-agent-kit/src/open_agent_kit/features/codebase_intelligence/retrieval/engine.py', 'auto-extracted,importance:medium,session:exploration', 5, NULL, '2026-01-25T16:43:13.875379', 1769377393, 1, '0b94f7b61ab67570', 'morningstar_chris');
