INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('b6055a52-69e9-469d-a67f-999e1e51dd24', '23a3b5d1-4021-425d-9b63-812aefc12ccd', 42, 'The `oak-ci-hook.sh` scripts use hardcoded paths to the MCP binary (`/usr/local/bin/mcp`). If the binary is installed in a different location (e.g., via `pip install mcp`), the script fails silently, and the agent receives an empty response.', 'gotcha', '/Users/chris/Repos/open-agent-kit/src/open_agent_kit/features/codebase_intelligence/hooks/cursor/oak-ci-hook.sh', 'auto-extracted,importance:medium,session:exploration', 5, NULL, '2026-01-25T16:43:14.082774', 1769377394, 1, '8edce7ba8b0c9ce8', 'morningstar_chris');
