INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('d7d065a3-41c3-4eaf-af0a-e54a642eb814', 'db06f42f-8d00-4c8c-a4c2-2a77a4535e5f', 56, 'Hook execution order is determined by the file system traversal order in `routes/hooks.py`. If a new hook file is added with a name that sorts before existing ones, it will execute earlier than intended. Developers must prefix hook filenames with a numeric order (e.g., `01-logging.json`) to guarantee deterministic ordering.', 'gotcha', 'Hook lifecycle', 'auto-extracted,importance:high,session:implementation', 8, NULL, '2026-01-25T16:45:50.981971', 1769377550, 1, '12206b677192cf39', 'morningstar_chris');
