INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('bd7a598e-b009-4830-a213-9b3aecf311d9', 'f4c052d2-2753-49d2-966a-0d989a9366b4', NULL, 'The store modules for activity and memory were split into separate files to isolate concerns: `activity/store.py` handles CRUD, recovery, and plan embedding for activity logs, while `memory/store.py` focuses on persisting codebase intelligence data. This separation aligns with the Single Responsibility Principle and makes unit testing easier.', 'decision', 'codebase_intelligence feature', 'auto-extracted,importance:high,session:implementation', 8, NULL, '2026-01-25T16:46:32.076843', 1769377592, 1, 'a89def1238f503d8', 'morningstar_chris');
INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('a3cd7990-b727-4f1b-84ce-b0c21f5d2fb3', 'f4c052d2-2753-49d2-966a-0d989a9366b4', NULL, 'SQLite was chosen as the persistence backend for both stores instead of a fullfeatured database like PostgreSQL. SQLite offers zero configuration, is filebased, and sufficient for the expected data volume in a single-user or small team deployment. The tradeoff is limited scalability and concurrent write performance.', 'discovery', 'codebase_intelligence feature', 'auto-extracted,importance:medium,session:implementation', 5, NULL, '2026-01-25T16:46:32.189851', 1769377592, 1, '136cf4ccc4a5503f', 'morningstar_chris');
