INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('c189670f-c444-4a83-a3fa-d45b08d14be4', '23a3b5d1-4021-425d-9b63-812aefc12ccd', 70, 'The `search` flag is not automatically interpreted by the agent; it must be explicitly checked when constructing a plan. If omitted, the agent will not consider using that tool for search-driven planning even if it is functionally capable.', 'gotcha', '/Users/chris/Repos/open-agent- kit/src/open_agent_kit/features/codebase_intelligence/daemon/mcp_tools.py', 'auto-extracted,importance:medium,session:exploration', 5, NULL, '2026-01-25T17:02:36.153983', 1769378556, 1, 'c96a3e33a273a944', 'morningstar_chris');
INSERT INTO memory_observations (id, session_id, prompt_batch_id, observation, memory_type, context, tags, importance, file_path, created_at, created_at_epoch, embedded, content_hash, source_machine_id) VALUES ('355752ee-0db2-4441-9a27-ba75900131df', '23a3b5d1-4021-425d-9b63-812aefc12ccd', 70, 'The `handler` entry in each tool metadata points to a function that is imported lazily inside the handler itself. This design avoids circular imports and keeps the module lightweight at import time.', 'discovery', '/Users/chris/Repos/open-agent-kit/src/open_agent_kit/features/codebase_intelligence/daemon/mcp_tools.py', 'auto-extracted,importance:medium,session:exploration', 5, NULL, '2026-01-25T17:02:36.275576', 1769378556, 1, 'f8062b25d5ea5b0c', 'morningstar_chris');
